setting.validationcode:
if (stripos($data,"http://" )!== false)
{
	return "Just a number, dash, and text please";
}
else if (strip_tags($data) != $data)
{
	return "Just a number, dash, and text please";
}
else if (strlen($data) == 0)
{
	return "This setting cannot be blank, you must enter a default page";
}
else if (substr($data, -1) == '/')
{
	return "No trailing Characters, please";
}
else
{
	return true;
}
