code.installcode:
require_once(DIR . '/includes/class_dbalter.php');
$db_alter = new vB_Database_Alter_MySQL($db);


if ($db_alter->fetch_table_info('user'))
{
	$db_alter->add_field(
		array(
			'name' => 'disablevbsocial',
			'type' => 'int',
			'attributes' => 'unsigned',
			'default' => '0'
		)
	);
}
unset($db_alter);

